import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom'
/* eslint-disable */

export default function ShowMenuItem(props) {
    const [itemConfig, setItemConfig] = useState([]);
    useEffect(() => {
        axios.get('http://localhost:3000/api/menu/item', { params: { menuItem: props.menuItem } })
            .then(res => {
                console.log(res.data);
                setItemConfig(res.data);
            });

    }, []);
    return (
    <div>
        <Link to="/showMenu">Back</Link>
        {
           itemConfig.map((m, key) =>
               <div key={key}>
                   <h3>{m.name}</h3>
                   <form key={key} class="form-check">
                       {m.size1 != "" ? <input type="radio" name="itemSize" class="form-check-input" /> : ""}
                       {m.size1}
                       {m.size2 != "" ? <input checked type="radio" name="itemSize" class="form-check-input" /> : ""}
                       {m.size2}
                       {m.size3 != "" ? <input type="radio" name="itemSize" class="form-check-input" /> : ""}
                       {m.size3}
                    </form>
                </div>
            )
        }
    </div>);
}
