import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import qs from 'qs';
import { BrowserRouter, Routes, Route, Link, useNavigate } from 'react-router-dom'
import QuantitySelector from './QuantitySelector';
import { CartContext } from './App'
/* eslint-disable */

export default function ShowMenuItem(props) {
    const [cart, setCart] = useContext(CartContext);
    const [itemConfig, setItemConfig] = useState([]);
    const [val1, setVal1] = useState(0);
    const [val2, setVal2] = useState(0);
    const [val3, setVal3] = useState(0);
    const [quantity, setQuantity] = useState(1);
    const navigate = useNavigate();
    useEffect(() => {
        axios.get('http://localhost:3000/api/menu/item',
            { params: { menuItem: props.menuItem } })
        .then(res => {
            console.log(res.data);
            setItemConfig(res.data);
        });
    }, []);
    function submit(e, key, item) {
        e.preventDefault();
        const data = [val1, val2, val3, quantity];
        console.log(data)
        const url = "http://localhost:3000/api/submitOrder";
        const options = {
            method: 'POST',
            headers: { 'content-type': 'application/x-www-form-urlencoded' },
            data: qs.stringify(data),
            url,
        };
        axios(options);
        console.log("item added to cart " + item.name)
        setCart([item]);
        navigate("/showMenu");
    }
    function changeRadio1() {
        setVal1(1);
        setVal2(0);
        setVal3(0);
    }

    function changeRadio2() {
        setVal1(0);
        setVal2(1);
        setVal3(0);
    }

    function changeRadio3() {
        setVal1(0);
        setVal2(0);
        setVal3(1);
    }
    return (
    <div>
        <Link to="/showMenu">
            <button className="btn btn-secondary m-1">Back</button>
        </Link>
        {
           itemConfig.map((item, key) =>
               <div className="" key={key}>
                   <h2>{item.name}</h2>
                   <form
                       key={key}
                       className="form-check"
                       onSubmit={(e) => submit(e, key, item)}>
                       {item.size1 != "" ?
                           <label>
                               <input
                                   type="radio"
                                   checked={val1}
                                   onChange={(e) => changeRadio1()}
                                   name="itemSize"
                                   className="form-check-input" />
                               {item.size1}
                           </label> : ""
                       }
                       {item.size1 != "" ? <br /> : ""}
                       {item.size2 != "" ?
                           <label>
                               <input
                                   type="radio"
                                   checked={val2}
                                   onChange={(e) => changeRadio2()}
                                   name="itemSize"
                                   className="form-check-input" />
                               {item.size2}
                           </label> : ""
                       }
                       {item.size1 != "" ? <br /> : ""}
                       {item.size3 != "" ?
                           <label>
                               <input
                                   type="radio"
                                   checked={val3}
                                   onChange={(e) => changeRadio3()}
                                   name="itemSize"
                                   className="form-check-input" />
                               {item.size3}
                           </label> : ""
                       }
                       {item.size1 != "" ? <br /> : ""}
                       <QuantitySelector
                           inputValue={quantity}
                           onInputValueChange={setQuantity} />
                       <input
                           type="submit"
                           className="btn btn-primary"
                           value={"Add " + quantity + " to cart"} />
                   </form>
                </div>
            )
        }
    </div>);
}
